const dealElement = (obj) => {
    const param = {}
    if (obj === null || obj === undefined || obj === '') {
        return param
    }
    for (const key in obj) {
        if (obj[key] !== null && obj[key] !== undefined && obj[key] !== '') {
            param[key] = obj[key]
        }
    }
    return param
}

